import { Layout, Card , Col, Row, Space, List, Typography, Image, Skeleton, Switch, Avatar  } from 'antd';
import banner from '../../images/banner4.jpg';
import { getNews }  from '../../config/api';
import { Link } from 'react-router-dom';
import { useEffect, useState } from 'react';
const { Paragraph, Title, Text } = Typography;
const { Meta } = Card;



function News1() {
    const [newsData, setNewsData] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const onChange = (key) => {
        console.log(key);
    };

    
    useEffect(() => {
        getNews()
            .then(response => {
                if (response.data && Array.isArray(response.data)) {
                    // 假设 uploadTime 是 ISO 格式的字符串，需要将其转换为 Date 对象进行比较
                    const sortedNewsData = response.data.sort((a, b) => new Date(b.upload_date) - new Date(a.upload_date));
                    setNewsData(sortedNewsData);
                } else {
                    setNewsData([]);
                }
                setLoading(false);
            })
            .catch(error => {
                setError(error);
                setLoading(false);
            });
        }, []);    
    // 对新闻数据按上传时间进行排序

    return (
        <Layout>
            <Layout>
                <Image
                    width='100%'
                    src={banner}
                    preview={false}
                />
            </Layout>
            <Layout style={{margin:"40px 0"}}>
                <Col offset={4} span={16}>
                    <List
                        pagination={{
                            pageSize: 6
                        }}
                        itemLayout="horizontal"
                        dataSource={newsData}
                        renderItem={(item) => (
                            <List.Item>
                                <List.Item.Meta
                                    style={{marginTop:"20px", marginBottom:"20px", marginRight:"20px"}}
                                    title=  { 
                                                <Link to={`/NewsDetails/${item.id}`} style={{ fontSize:"1.2vw"}}>
                                                    {item.title}
                                                </Link>
                                            }
                                    avatar= {
                                                item.images && item.images.length > 0 ? (
                                                    <Image preview={false} style={{width:"20vw"}} src={item.images[0].image} />
                                                ) : (
                                                    <Image preview={false} style={{width:"20vw"}} fallback="data:image/png;base64,"/> // 可以根据需要设置一个默认的 avatar 图标
                                                )
                                            }
                                    description={
                                                    <Text style={{ color: "grey", fontSize:"0.8vw"}}>
                                                        {item.content.split('\r\n')[0]}
                                                    </Text>
                                                }
                                />
                                <div style={{ color: "grey", fontSize:"1vw" }}>{new Date(item.upload_date).toLocaleDateString()}</div>
                            </List.Item>
                        )}
                    />
                </Col>
            </Layout>
        </Layout>
        
    );
}

export default News1;